Page._content:
/* Simple and reliable like button styles */
.post-like-container {
  margin: 20px 0;
  text-align: center;
}

.post-like-btn {
  display: inline-flex !important;
  align-items: center !important;
  gap: 8px !important;
  padding: 10px 20px !important;
  background-color: #ff4757 !important;
  color: white !important;
  border: none !important;
  border-radius: 50px !important;
  cursor: pointer !important;
  font-size: 14px !important;
  font-weight: bold !important;
  transition: all 0.3s ease !important;
  box-shadow: 0 2px 8px rgba(255, 71, 87, 0.3) !important;
  /* Ensure button is not overridden by other styles */
  width: auto !important;
  height: auto !important;
  line-height: normal !important;
  overflow: visible !important;
  outline: none !important;
  font-family: inherit !important;
  text-decoration: none !important;
  margin: 10px auto !important;
  display: block !important;
}

.post-like-btn:hover {
  background-color: #ff3742;
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(255, 71, 87, 0.4);
}

.post-like-btn:active {
  transform: translateY(0);
  box-shadow: 0 2px 6px rgba(255, 71, 87, 0.3);
}

.post-like-btn:disabled {
  background-color: #dfe4ea;
  color: #636e72;
  cursor: not-allowed;
  transform: none;
  box-shadow: none;
}

.post-like-btn i {
  font-size: 16px;
}

/* Liked state */
.post-like-btn.liked {
  background-color: #57606f;
  box-shadow: 0 2px 8px rgba(87, 96, 111, 0.3);
}

.post-like-btn.liked:hover {
  background-color: #57606f;
  transform: none;
  box-shadow: 0 2px 8px rgba(87, 96, 111, 0.3);
}

/* Ensure like count is visible */
.like-count {
  font-size: 14px;
  font-weight: bold;
}

/* Home page like button */
.post-card-like-btn {
  display: inline-flex;
  align-items: center;
  gap: 5px;
  padding: 5px 10px;
  background: none;
  border: none;
  color: #636e72;
  cursor: pointer;
  font-size: 14px;
  border-radius: 20px;
  transition: all 0.3s ease;
  outline: none;
  font-family: inherit;
}

.post-card-like-btn:hover {
  background-color: #f1f2f6;
  color: #ff4757;
}

.post-card-like-btn.liked {
  color: #ff4757;
  background-color: #ffeaea;
}

.post-card-like-btn:disabled {
  cursor: not-allowed;
  opacity: 0.6;
}

/* Post card like count */
.post-card-like-count {
  display: inline-flex;
  align-items: center;
  gap: 5px;
  color: #636e72;
  cursor: pointer;
  transition: color 0.3s ease;
}

.post-card-like-count:hover {
  color: #ff4757;
}

.post-card-like-count.liked {
  color: #ff4757;
}

.post-card-like-count i {
  font-size: 16px;
}

